import React, { useState } from "react";
import axios from "axios";
import Header from "./Header";

function App() {
    const [file, setFile] = useState(null);
    const [imageUrl, setImageUrl] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const [imageBlob, setImageBlob] = useState(null);
     const [darkMode, setDarkMode] = useState(false); // Dark mode state

    const toggleDarkMode = () => {
        setDarkMode((prevMode) => !prevMode);
        document.documentElement.classList.toggle("dark"); // Toggles the "dark" class on the HTML element
    };

    const sampleImages = [
        "/sample1.jpg",
        "/sample2.jpg",
        "/sample3.jpg",
    ];

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setErrorMessage("");
        setImageUrl("");
    };

    const handleUpload = async (fileToUpload) => {
        if (!fileToUpload && !file) {
            setErrorMessage("Please select an image first!");
            return;
        }

        const formData = new FormData();
        formData.append("file", fileToUpload || file);

        try {
            const response = await axios.post(
                "http://127.0.0.1:5000/upload",
                formData,
                { responseType: "blob" }
            );

            const blob = new Blob([response.data], { type: "image/jpeg" });
            setImageBlob(blob);
            setImageUrl(URL.createObjectURL(blob));
        } catch (error) {
            console.error("Error uploading image:", error);
            setErrorMessage("Something went wrong. Please try again.");
        }
    };

    const handleSampleClick = (sampleUrl) => {
        fetch(sampleUrl)
            .then((res) => res.blob())
            .then((blob) => handleUpload(blob))
            .catch((err) => {
                console.error("Error loading sample image:", err);
                setErrorMessage("Failed to load the sample image.");
            });
    };

    return (
        <div>
            <Header />
            <main className="p-4 bg-gray-100 min-h-screen flex items-center justify-center">
                <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md">
                    <h2 className="text-lg font-semibold text-gray-800 mb-4">
                        Upload an Image
                    </h2>
                    <div className="mb-4">
                        <input
                            type="file"
                            onChange={handleFileChange}
                            className="block w-full text-sm text-gray-500 
                                   file:mr-4 file:py-2 file:px-4 
                                   file:rounded-full file:border-0 
                                   file:text-sm file:font-semibold 
                                   file:bg-blue-50 file:text-blue-700
                                   hover:file:bg-blue-100"
                        />
                    </div>
                    <button
                        onClick={() => handleUpload(null)}
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded"
                    >
                        Upload Image
                    </button>

                    <div className="mt-6">
                        <h2 className="text-lg font-semibold text-gray-800">
                            Try Our Sample Images
                        </h2>
                        <div className="flex space-x-4 mt-4">
                            {sampleImages.map((url, index) => (
                                <img
                                    key={index}
                                    src={url}
                                    alt={`Sample ${index + 1}`}
                                    className="w-20 h-20 object-cover rounded cursor-pointer hover:ring-2 hover:ring-blue-500"
                                    onClick={() => handleSampleClick(url)}
                                />
                            ))}
                        </div>
                    </div>

                    {errorMessage && (
                        <p className="text-red-500 text-sm mt-4">{errorMessage}</p>
                    )}
                    {imageUrl && (
                        <div className="mt-6">
                            <h2 className="text-lg font-semibold text-gray-800">
                                Processed Image:
                            </h2>
                            <img
                                src={imageUrl}
                                alt="Processed"
                                className="mt-4 rounded shadow-md w-full"
                            />
                            <a
                            href={URL.createObjectURL(imageBlob)}
                            download="processed-image.jpg"
                            className="inline-block mt-4 bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
                            >
                            Download Image
                        </a>
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
}

export default App;
