import React, { useState } from "react";
import axios from "axios";
import Header from "./Header";

function App() {
    const [file, setFile] = useState(null);
    const [imageUrl, setImageUrl] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const [imageBlob, setImageBlob] = useState(null); // Store blob for download

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setErrorMessage("");
        setImageUrl(""); // Clear previous image
        setImageBlob(null); // Clear blob
    };

    const handleUpload = async () => {
        if (!file) {
            setErrorMessage("Please select an image first!");
            return;
        }

        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await axios.post(
                "http://127.0.0.1:5000/upload",
                formData,
                { responseType: "blob" }
            );

            const blob = new Blob([response.data], { type: "image/jpeg" });
            setImageBlob(blob); // Save blob for download
            setImageUrl(URL.createObjectURL(blob)); // Display processed image
        } catch (error) {
            console.error("Error uploading image:", error);
            setErrorMessage("Something went wrong. Please try again.");
        }
    };

    return (
        <div>
            <Header />
            <main className="p-4 bg-gray-100 min-h-screen flex items-center justify-center">
                <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md">
                    <h2 className="text-lg font-semibold text-gray-800 mb-4">
                        Upload an Image
                    </h2>
                    <div className="mb-4">
                        <input
                            type="file"
                            onChange={handleFileChange}
                            className="block w-full text-sm text-gray-500 
                                       file:mr-4 file:py-2 file:px-4 
                                       file:rounded-full file:border-0 
                                       file:text-sm file:font-semibold 
                                       file:bg-blue-50 file:text-blue-700
                                       hover:file:bg-blue-100"
                        />
                    </div>
                    <button
                        onClick={handleUpload}
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded"
                    >
                        Upload Image
                    </button>
                    {errorMessage && (
                        <p className="text-red-500 text-sm mt-4">{errorMessage}</p>
                    )}
                    {imageUrl && (
                        <div className="mt-6">
                            <h2 className="text-lg font-semibold text-gray-800">
                                Processed Image:
                            </h2>
                            <img
                                src={imageUrl}
                                alt="Processed"
                                className="mt-4 rounded shadow-md w-full"
                            />
                            <a
                                href={URL.createObjectURL(imageBlob)} // Download link for blob
                                download="processed-image.jpg" // Suggested file name
                                className="inline-block mt-4 bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
                            >
                                Download Image
                            </a>
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
}

export default App;
